//sns
.sns {
  position: relative;
  width: 1300px;
  height: 300px;
  margin: 0 auto;
  margin-bottom: 200px;
  overflow: hidden;
}
.sns .swiper {
  position: absolute;
  left: -100px;
  top: 0;
  width: 1500px;
}
.sns .swiper .swiper-wrapper .swiper-slide {
  width: 300px;
  height: 300px;
  position: relative;
  margin-right: 15px;
  cursor: pointer;
}
.sns .swiper .swiper-wrapper .swiper-slide img {
  width: 100%;
  height: 100%;
}
.sns .swiper .swiper-wrapper .swiper-slide p {
  height: 70px;
  position: absolute;
  left: 0;
  bottom: -100px;
  text-align: center;
  background-color: rgba(255, 255, 255, 0.4);
  padding: 5px 0;
  transition: 0.5s;
  border-radius: 20px 20px 0 0;
}
.sns .swiper .swiper-wrapper .swiper-slide .add_cart {
  position: absolute;
  width: 35px;
  height: 35px;
  right: 8px;
  top: 8px;
  border: 1px solid #000;
  border-radius: 50%;
  padding: 3px;
  box-sizing: border-box;
}
.sns .swiper .swiper-wrapper .swiper-slide .add_cart img {
  width: 100%;
  height: 100%;
}
.sns .swiper .swiper-wrapper .swiper-slide:hover p {
  bottom: 0;
}
.sns .swiper .swiper-button-prev,
.sns .swiper .swiper-button-next {
  background-color: #fff;
  opacity: 0.7;
  color: black;
  width: 90px;
}
.sns .swiper .swiper-button-prev {
  position: absolute;
  left: 100px;
  top: 22px;
  height: 100%;
}
.sns .swiper .swiper-button-next {
  position: absolute;
  right: 100px;
  top: 22px;
  height: 100%;
}

