.footer {
  @include size(1300px, 400px);
  margin: 0 auto;
  background-color: #444;
  color: #fff;
  padding: 25px;
  box-sizing: border-box;
  overflow: hidden;
  font-size: 12px;
  .footer_inner {
    @include size(1250px, 350px);
    margin: 0 auto;
    border: 2px solid #ddd;
    box-sizing: border-box;
    display: flex;
    justify-content: space-between;
    .footer_left {
      padding-left: 115px;
      .footer_menu {
        margin-bottom: 30px;
        ul {
          display: flex;
          margin-top: 35px;

          li {
            margin-right: 30px;
            cursor: pointer;
          }
        }
      }
      .info {
        p {
          margin-bottom: 10px;
        }
        span {
          margin-left: 30px;
        }
        p:nth-child(2) span:last-child {
          text-decoration: underline;
        }
        p:nth-child(4) {
          margin-bottom: 40px;
        }
        p:last-child span {
          text-decoration: underline;
        }
      }
    }
    .footer_right {
      margin-right: 150px;
      .logo_box {
        margin-top: 30px;
        @include size(140px, 70px);
        font-size: 12px;
        margin-bottom: 70px;
        p {
          border: 1px solid #fff;
          text-align: center;
          box-sizing: border-box;
        }
        p:last-child {
          border-top: none;
          white-space: nowrap;
          box-sizing: border-box;
          font-size: 10px;
          overflow: hidden;
        }
      }
      .sns_icon {
        display: flex;
        overflow: hidden;
        /*  .insta {
        } */
      }
      .sns_icon div {
        @include size(40px, 40px);

        box-sizing: border-box;
        overflow: hidden;
      }
    }
  }
}

///sub_list
.sub_list {
  @include size(1120px, auto);
  margin: 0 auto;
  margin-bottom: 150px;
  margin-top: 120px;
  .path_box {
    display: flex;
    justify-content: space-between;
    margin-bottom: 30px;
    .path {
      display: flex;
      //margin-left: 10px;
    }
    .sort {
      margin-left: auto;
      //margin-right: 10px;
      select {
        border: none;
        background-color: $main;
      }
    }
  }
  .item_list {
    display: flex;
    flex-wrap: wrap;
    margin-bottom: 100px;
    .item {
      background-color: #fff;
      box-sizing: border-box;
      @include size(360px, 450px);
      margin-right: 20px;
      margin-bottom: 60px;
      padding-top: 5px;
      .img_box {
        @include size(250px, 340px);
        margin: 0 auto;
        img {
          box-sizing: border-box;
        }
      }
      .text {
        text-align: center;
        margin: 0 auto;
        p {
          margin-bottom: 20px;
        }
        .en_title {
          @include en;
          font-size: 14px;
        }
        .ko_title {
          @include ko;
          font-size: 14px;
        }
        .price {
          font-size: 12px;
        }
      }
    }
    .item:nth-child(3n) {
      margin-right: 0;
    }
    .item:hover p {
      display: none;
    }
    .item:hover .preview {
      display: block;
    }
    .preview {
      background-color: #000;
      width: 290px;
      height: 34px;
      display: none;
      color: #fff;
      text-align: center;
      margin: 38px auto;
      border-radius: 8px;
      line-height: 34px;
      cursor: pointer;
    }
    .preview:hover {
      background-color: #fff;
      color: #000;
      border: 1px solid #eee;
    }
  }

  //
  .page {
    width: 250px;
    margin: 0 auto;
    .pagination {
      display: flex;
      justify-content: space-between;
      li {
        margin-right: 10px;
        @include size(25px, 25px);
        font-size: 12px;
        cursor: pointer;
        border: 0.5px solid #ccc;
        box-sizing: border-box;
        text-align: center;
        line-height: 25px;
        background-color: #fff;
      }
      li:last-child {
        margin-right: 0;
      }
      li:nth-child(1) {
        background-color: #eee;
      }
      li:nth-child(2) {
        background-color: #eee;
      }
      li:nth-child(8) {
        background-color: #eee;
      }
      li:nth-child(9) {
        background-color: #eee;
      }
    }
  }
}
