.footer {
  @include size(1300px, 400px);
  margin: 0 auto;
  background-color: #444;
  color: #fff;
  padding: 25px;
  box-sizing: border-box;
  overflow: hidden;
  font-size: 12px;
  .footer_inner {
    @include size(1250px, 350px);
    margin: 0 auto;
    border: 2px solid #ddd;
    box-sizing: border-box;
    display: flex;
    justify-content: space-between;
    .footer_left {
      padding-left: 115px;
      .footer_menu {
        margin-bottom: 30px;
        ul {
          display: flex;
          margin-top: 35px;

          li {
            margin-right: 30px;
            cursor: pointer;
          }
        }
      }
      .info {
        p {
          margin-bottom: 10px;
        }
        span {
          margin-left: 30px;
        }
        p:nth-child(2) span:last-child {
          text-decoration: underline;
        }
        p:nth-child(4) {
          margin-bottom: 40px;
        }
        p:last-child span {
          text-decoration: underline;
        }
      }
    }
    .footer_right {
      margin-right: 150px;
      .logo_box {
        margin-top: 30px;
        @include size(140px, 70px);
        font-size: 12px;
        margin-bottom: 70px;
        p {
          border: 1px solid #fff;
          text-align: center;
          box-sizing: border-box;
        }
        p:last-child {
          border-top: none;
          white-space: nowrap;
          box-sizing: border-box;
          font-size: 10px;
          overflow: hidden;
        }
      }
      .sns_icon {
        display: flex;
        overflow: hidden;
        /*  .insta {
          } */
      }
      .sns_icon div {
        @include size(40px, 40px);

        box-sizing: border-box;
        overflow: hidden;
      }
    }
  }
}

