  position: relative;
  overflow: hidden;
  p {
    position: absolute;
    width: 100%;
    color: #fff;
    animation: rolling 60s infinite both;
    line-height: 40px;
    span {
      cursor: pointer;
    }
    span:hover {
      text-decoration: underline;
    }
  }
  P:hover {
    animation-play-state: paused;
  }
  P:hover ~ p {
    animation-play-state: paused;
  }
  .roll_text1 {
    left: 0;
  }
  .roll_text2 {
    left: 100%;
