  .close {
    display: block;
    @include size(30px, 30px);
    position: absolute;
    top: 0;
    right: -25%;
    color: #fff;
  }
}

@keyframes rolling {
  0% {
    transform: translateX(100%);
  }
  20% {
    transform: translateX(0%);
  }
  40% {
    transform: translateX(-100%);
  }
  60% {
